"use client";

import { ICartItem, ICartState, Product, Variation } from "@/lib/models";
import useCart from "@/lib/components/Cart/useCart";
import { ReactElement } from "react";

interface IProps {
  product: Product;
  variation: Variation;
  // Content must be rendered server side because it may call Square APIs, thus it cannot be generated by this component
  content: ReactElement;
}

export default function ClientAddToCartButton({
  product,
  variation,
  content,
}: IProps) {
  const addItem = useCart((state: ICartState) => state.addItem);
  const item: ICartItem = {
    productId: product.id,
    variationId: variation.id,
    quantity: 1,
  };
  return (
    <a
      className="group rounded-lg border px-5 py-4 my-4 border-gray-300 transition-colors hover:border-gray-400 hover:bg-gray-100"
      style={{ cursor: "pointer" }}
      onClick={() => addItem(item)}
    >
      <div className="flex w-52">
        {content}
        <h2 className="mb-3 text-2xl pl-5 font-medium h-fill">
          <span className="inline-block transition-transform group-hover:translate-x-1 motion-reduce:transform-none">
            -&gt;
          </span>
        </h2>
      </div>
    </a>
  );
}
